import React, { Component } from 'react'
import {Row} from 'reactstrap';

let valido = null;

var callback = function () {
    console.log('Done!');
};

var verifyCallback = function (response) {
    valido = response;
};

const handleFormSubmit = (e, props) => { //ENVIO DE DATOS AL BACK
    e.preventDefault();
    if (valido != null) {
        props.onAuth(e.target.elements.username.value, e.target.elements.password.value);
        props.history.push("/profile"); //Ruta a la cual me redigira si el login es verdadero
    } 
}

function Login(props){

        return (
          
            <div>
                <form onSubmit={(event) => handleFormSubmit(event, props)} style={{ marginTop: '20px' }} >
                    <Row>
                        <label htmlFor="username" >Username</label>
                        <input type="text"
                        onChange={this.props.handleLoginChange} 
                        value={this.props.username} 
                        name="username"
                        id="username"
                        placeholder="Username" />
                    </Row>
                    <Row>
                        <label htmlFor="password" >Password</label>
                        <input type="password"
                        onChange={this.handlePasswordChange} 
                        value={this.state.password} 
                        name="password"
                        id="password"
                        placeholder="Password" />
                    </Row>
                    <button type='submit'>Login</button>
                </form>
            </div>
        )
}

export default LoginUser
