import React from 'react';
import { withRouter, Redirect, Link } from 'react-router-dom';
import Footer from './Footer';

class LoginForm extends React.Component {
  
  state = {
    username: '',
    password: ''
  };

  componentDidMount() { 
  
    let data ; 

    axios.get('http://localhost:8000/wel/') 
    .then(res => { 
        data = res.data; 
        this.setState({ 
            details : data     
        }); 
    }) 
    .catch(err => {}) 
  } 

  handle_change = e => {
    const name = e.target.name;
    const value = e.target.value;
    this.setState(prevstate => {
      const newState = { ...prevstate };
      newState[name] = value;
      return ne
    });wState;
  };

  render() {
    return (
      <form onSubmit={e => this.props.handle_login(e, this.state)}>
        <h4>Log In</h4>
        <label htmlFor="username">Username</label>
        <input
          type="text"
          name="username"
          value={this.state.username}
          onChange={this.handle_change}
        />
        <label htmlFor="password">Password</label>
        <input
          type="password"
          name="password"
          value={this.state.password}
          onChange={this.handle_change}
        />
        <input type="submit" />
      </form>
    );
  }
}


function Login() {

  return (
    
    <div className="row">
      <div className="col-lg-8">
        <img src="https://cdn.pixabay.com/photo/2020/03/03/12/15/mask-4898571_960_720.jpg" className="img-fluid" alt="Responsive image" width="auto" height="auto"></img>
      </div>

      <div className="col-lg-4">
        <form style={{ width: "80%", marginLeft: "10%", marginRight: "10%", marginTop: "20%" }}>
          <div class="form-group">
          <img src={process.env.PUBLIC_URL + '/logo3w.png'} alt="logo" className="img-fluid" alt="Responsive image" width="auto" height="auto" />
            <label for="exampleInputEmail1">Username</label>
            <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Username"></input>
          </div>
          <div class="form-group">
            <label for="exampleInputPassword1">Password</label>
            <input type="password" class="form-control" id="exampleInputPassword1" placeholder="Password"></input>
          </div>
          <div class="form-check">
            <input type="checkbox" class="form-check-input" id="exampleCheck1"></input>
            <label class="form-check-label" for="exampleCheck1">Check me out</label>
          </div>
          <button type="submit" class="btn btn-primary">Login</button>
        </form>
      </div>
      <div className="col-lg-12"><Footer/></div>
    </div>
  );
}

export default Login;
