import React from 'react'
import { connect } from 'react-redux';
import { Layout } from 'antd';
import * as actions from '../store/action/auth';
import { withRouter } from 'react-router-dom';


let valido = null;

const handleFormSubmit = (e, props) => { //ENVIO DE DATOS AL BACK
    e.preventDefault();
    if (valido != null) {
        props.onAuth(e.target.elements.username.value, e.target.elements.password.value);
        props.history.push("/profile"); //Ruta a la cual me redigira si el login es verdadero
    } 
}

function LoginUser(props){
    if (props.auth.authenticate == true && props.auth.usuario != 'Revisor') {
        return (<Redirect to="/profile" />
        )
    } else if (props.auth.usuario === 'Revisor'){
        return (<Redirect to="/RegistroConsumo" />
        )
    }


    return (
        <Layout className="layout">
            <main role="main" className="flex-shrink-0 mt-5">
                <div className="row">
                    <div className="col-lg-5">
                        <div className="container" style={{ marginTop: 80, marginBottom: 70, textAlign: "center" }}>
                            <form onSubmit={(event) => handleFormSubmit(event, props)} style={{ marginTop: '20px' }}>
                                <div className="form-group">
                                    <input name="username" className="form-control" placeholder="username" ></input>
                                </div>
                                <div className="form-group">
                                    <input name="password" type="password" className="form-control" placeholder="password" ></input>
                                </div>
                                <div>
                                    <button type="submit" className="btn btn-success btn-block">sumitd</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </main>
            <div>
            </div>
        </Layout>
    );
}

const mapStateToProps = state => {
    return {
        auth: state.reducer
    }
};

const mapDispatchToProps = dispatch => {
    return {
        onAuth(username, password) {
            dispatch(actions.authLogin(username, password))
        }
    }
};

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(LoginUser));
